import React, { useState } from "react";
import './navigation-panel.scss';

function Navigation() {

    const [navList, setNavlist] = useState(false);
    const showNavlist = () => setNavlist(!navList);

    hamburger = document.getElementById('hamburger');
    nav = document.getElementById('nav-list');
    hamburger.addEventListener('click', function (event) {
        nav.classList.toggle('open');
    });

    document.addEventListener('click', function (event) {
        if (nav.classList.contains('open') && !event.target.isEqualNode(hamburger) && !event.target.isEqualNode(nav) && !nav.contains(event.target)) {
            nav.classList.remove('open');
        }
    });

    return (
        <div className={navList ? 'navlist active' : 'navlist'}>
            <button id="hamburger" className="hamburger" type="button" onClick={showNavlist}>
                <div></div>
            </button>

            <ul id='nav-list' onClick={showNavlist}>
                <li>
                    <span class="nav-text">
                        Dashboard
                    </span>
                </li>
                <li>
                    <span class="nav-text">
                        Fleets
                    </span>
                </li>
                <li>
                    <span class="nav-text">
                        Clusters
                    </span>
                </li>
            </ul>
        </div>
    );
}

export default Navigation;
